import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, TextInput, Text, Button, Image, View } from 'react-native';

export default function App() {
  const [text, onChangeText] = React.useState('');
  return (
    <View style={styles.container}>

      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        placeholder="Introduce nombre personaje de Rick y Morty"
        value={text} 
        />

        <Button
        title="Buscar"
        onPress={() => fetch('https://rickandmortyapi.com/api/character')
            .then(response => response.json())
            .then(json => {
              return json;
            })
            .catch(error => {
              console.error(error);
            }) 
        }
        />

        <Image
        style={styles.imgRick}
        source={{
          uri: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
        }}
        />

        <Text>By Johan Cruz Huertas</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  imgRick: {
    width: 50,
    height: 50,
  },
});
