import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, TextInput, Text, Button, Image, View } from 'react-native';

export default function App() {
  const [text, onChangeText] = React.useState('');
  const mySstyles = StyleSheet.create({
    container: {
      paddingTop: 50,
    },
    tinyLogo: {
      width: 50,
      height: 50,
    },
  });

  async function fetchData() {
    try {
      const response = await fetch('https://my-api.com/data');
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  }

  const searchImage = () => {
    setUri(require('../pictures/sections/hearth_full.png'));
   }

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        placeholder="Introduce nombre personaje de Rick y Morty"
        value={text} 
        />
        <Button
        title="Buscar"
        onPress={this.searchImage}
        />
        <Image
        style={mySstyles.tinyLogo}
        source={{
          uri: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
        }}
        />
        <Text>By Johan Cruz Huertas</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
