        style={mySstyles.tinyLogo}
        source={{
          uri: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
        }}
        />
        <Text>By Johan Cruz Huertas</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
